import Link from 'next/link'

const Index = props => {
  <>
    <h1>トップページ</h1>
    <p>現在はサーバー側で{props.isServer.toString()? 'ござる' : 'ござらない'}</p>
    <Link href="/about">
      <a>About</a>
    </Link>
  </>
}

Index.getInitialProps = async function () {
  return {
    isServer: typeof window === 'undefined'
  }
}

export default Index
